import React, { useState, useEffect } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const TeaCard = ({ tea, onDragStart }) => {
  return (
    <div
      className="bg-white p-4 rounded-lg shadow-md cursor-move"
      draggable
      onDragStart={(e) => onDragStart(e, tea)}
    >
      <h3 className="text-lg font-bold">{tea.name}</h3>
      <div className="mt-2">
        <p>香り: {tea.aroma}/10</p>
        <p>耐病性: {tea.disease_resistance}/10</p>
        <p>収量: {tea.yield}/10</p>
        <p>味: {tea.flavor}/10</p>
      </div>
    </div>
  );
};

const DropZone = ({ onDrop, children }) => {
  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const tea = JSON.parse(e.dataTransfer.getData('tea'));
    onDrop(tea);
  };

  return (
    <div
      className="border-2 border-dashed border-gray-300 p-4 rounded-lg min-h-[200px]"
      onDragOver={handleDragOver}
      onDrop={handleDrop}
    >
      {children}
    </div>
  );
};

function App() {
  const [teaVarieties, setTeaVarieties] = useState([]);
  const [selectedTeas, setSelectedTeas] = useState([]);
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    fetchTeaVarieties();
  }, []);

  const fetchTeaVarieties = async () => {
    try {
      const response = await axios.get('http://localhost:5001/api/tea-varieties');
      setTeaVarieties(response.data);
    } catch (error) {
      console.error('Error fetching tea varieties:', error);
    }
  };

  const handleDragStart = (e, tea) => {
    e.dataTransfer.setData('tea', JSON.stringify(tea));
  };

  const handleDrop = (tea) => {
    if (selectedTeas.length < 2) {
      setSelectedTeas([...selectedTeas, tea]);
    }
  };

  const handleHybridize = async () => {
    if (selectedTeas.length === 2) {
      try {
        const response = await axios.post('http://localhost:5001/api/hybridize');
        const newTea = response.data;
        setTeaVarieties([...teaVarieties, newTea]);
        setSelectedTeas([]);
        updateChartData();
      } catch (error) {
        console.error('Error hybridizing:', error);
      }
    }
  };

  const updateChartData = () => {
    const labels = teaVarieties.map(tea => tea.name);
    const data = {
      labels,
      datasets: [
        {
          label: '香り',
          data: teaVarieties.map(tea => tea.aroma),
          backgroundColor: 'rgba(255, 99, 132, 0.5)',
        },
        {
          label: '耐病性',
          data: teaVarieties.map(tea => tea.disease_resistance),
          backgroundColor: 'rgba(54, 162, 235, 0.5)',
        },
        {
          label: '収量',
          data: teaVarieties.map(tea => tea.yield),
          backgroundColor: 'rgba(75, 192, 192, 0.5)',
        },
        {
          label: '味',
          data: teaVarieties.map(tea => tea.flavor),
          backgroundColor: 'rgba(153, 102, 255, 0.5)',
        },
      ],
    };
    setChartData(data);
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold mb-8 text-center">お茶品種改良シミュレーター</h1>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          <div>
            <h2 className="text-xl font-semibold mb-4">品種一覧</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
              {teaVarieties.map(tea => (
                <TeaCard
                  key={tea.id}
                  tea={tea}
                  onDragStart={handleDragStart}
                />
              ))}
            </div>
          </div>

          <div>
            <h2 className="text-xl font-semibold mb-4">交配エリア</h2>
            <DropZone onDrop={handleDrop}>
              {selectedTeas.map(tea => (
                <TeaCard key={tea.id} tea={tea} />
              ))}
              {selectedTeas.length === 2 && (
                <button
                  className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
                  onClick={handleHybridize}
                >
                  交配する
                </button>
              )}
            </DropZone>
          </div>
        </div>

        {chartData && (
          <div className="mt-8">
            <h2 className="text-xl font-semibold mb-4">品種特性比較</h2>
            <div className="bg-white p-4 rounded-lg shadow">
              <Bar data={chartData} />
            </div>
          </div>
        )}
      </div>
    </DndProvider>
  );
}

export default App;
